using System;
using System.Diagnostics;
using NUnit.Framework;

namespace ElectionGuard.Tests
{
    [TestFixture]
    public class TestElection
    {
        [Test]
        public void Test_Can_Deserlialze_Ciphertext_Election_Context()
        {
            // Arrange
            string data = "{\"crypto_base_hash\":\"B8CF9A8915BDB19C681AFBCDD1797F2CF360F723843D977D0E1B280CA2B24245\",\"crypto_extended_base_hash\":\"731052175CAE2EF7CFE0E827C65BB585CD8A9CB243320EB34C09B5DF3F75F982\",\"commitment_hash\":\"02\",\"manifest_hash\":\"02\",\"elgamal_public_key\":\"\",\"number_of_guardians\":1,\"quorum\":1}";

            // Act
            var result = new CiphertextElectionContext(data);

            // Assert
            Assert.That(result.CommitmentHash.ToHex() == "02");
            Assert.That(result.ManifestHash.ToHex() == "02");
        }
    }
}
